    val listData: ArrayList<Teknik>
    get(){
        val list = ArrayList<Teknik>()
        for (aData in data){
            val teknik = Teknik()
            teknik.name = aData[0]
            teknik.photo = aData[1]
            teknik.pengertain =aData[2]
            teknik.bahan =aData[3]
            teknik.cara =aData [4]
            teknik.kelebihan =aData[5]
            teknik.kekurangan = aData[6]

            list.add(teknik)
        }
        return list
    }
